from juloserver.julo.models import JuloContactDetail



from juloserver.julo.models import FaqSubTitle



from juloserver.julo.models import FaqItem



from juloserver.julo.models import FaqSection



def add_assign_agent_settings(apps, schema_editor):
    
    
    
    
    # clean all records
    JuloContactDetail.objects.all().delete()
    FaqSubTitle.objects.all().delete()
    FaqItem.objects.all().delete()
    FaqSection.objects.all().delete()
    # store new data
    for section in FAQ_DATA:
        section_obj = FaqSection.objects.create(title=section['section_title'], order_priority=section['order_priority'])
        for item in section['items']:
            if item.get('is_contact') == True:
                JuloContactDetail.objects.create(
                    section=section_obj,
                    title=item['title'],
                    description=item['description'],
                    phone_numbers=[item['phone_number']],
                    contact_us_text=item['contact_us'],
                    chat_availability=item['chat_timings'],
                    address=item['address'],
                    image_url=item['image_url'],
                    order_priority=item['order_priority']
                )
            else:
                FaqItem.objects.create(
                    section=section_obj,
                    question=item['question'],
                    description=item['description'],
                    order_priority=item['order_priority']
                )


